    public static ArrayList<NabiRasulItem> getListData(){
        ArrayList<NabiRasulItem> list=new ArrayList<>();
        for (int position=0;position<namaNabiRasul.length;position++){
            NabiRasulItem nabiRasulItem =new NabiRasulItem();
            nabiRasulItem.setName(namaNabiRasul[position]);
            nabiRasulItem.setDetail(detailNabiRasul[position]);
            nabiRasulItem.setPhoto(nabiGambar[position]);
            nabiRasulItem.setKey_id(key_id[position]);
            nabiRasulItem.setFavStatus(fav_status[position]);
            nabiRasulItem.setUmur(Umur[position]);
            nabiRasulItem.setUmat(Umat[position]);
            list.add(nabiRasulItem);
        }
        return list;
    }
